-- CTE to select fields from original database and limit data usage. Tag fiels-- is processed so that each row has a unique tagWITH questions_subset AS (    SELECT         id,         TIMESTAMP_TRUNC(creation_date, QUARTER) AS creation_quarter,        unique_tag    FROM posts_questions    CROSS JOIN UNNEST(SPLIT(tags, '|')) AS unique_tag    WHERE accepted_answer_id IS NOT NULL),-- CTE to list the 10 most common tags for questions with answersmost_common_tags AS(    SELECT        unique_tag,        COUNT(*) AS tag_predominance    FROM questions_subset    GROUP BY unique_tag    ORDER BY tag_predominance DESC    LIMIT 8)-- Query to get the dayly cumulative number of questions for the ten most common-- tags in the database for questions with answersSELECT     unique_tag,    creation_quarter,    SUM(COUNT(*)) OVER(PARTITION BY unique_tag ORDER BY creation_quarter) AS cumulative_questionsFROM questions_subsetWHERE unique_tag IN (    SELECT         unique_tag     FROM most_common_tags)GROUP BY unique_tag, creation_quarterORDER BY unique_tag, creation_quarter;